// **************************************************************************************************************************
function showFront(event) {
	selectionType = document.getElementById("Selections1").value; // Gets the users selection in the Selections1 pop up menu.
	generateType = document.getElementById("Generate1").value; // Gets the users selection in the Generate1 pop up menu.
	colorType = document.getElementById("Color1").value; // Gets the users selection in the Color1 pop up menu.
    updateCheck = document.getElementById("Update1").value; // Gets the users selection in the Color1 pop up menu.
	if (window.widget) { // Saves the attributes decided by the user in Dashboard.
		widget.setPreferenceForKey(selectionType, "selectionType");
		widget.setPreferenceForKey(generateType, "generateType");
		widget.setPreferenceForKey(colorType, "colorType");
        widget.setPreferenceForKey(updateCheck, "updateCheck");
	}
	faceColor(); // Calls faceColor to set the color of the widget's face.
    var front = document.getElementById("front"); // The following code transitions the widget from back to front.
    var back = document.getElementById("back");
    if (window.widget) {
        widget.prepareForTransition("ToFront");
    }
    front.style.display="block";
    back.style.display="none";
    if (window.widget) {
        setTimeout('widget.performTransition();', 0);
    } // End of widget transition code.
	if (generateType == 'A') { // If user wants a quote generated when Dashboard is shown.
		generateQuote(); // Calls generateQuote() to show a new quote on screen.
	}
    updateNumber = 50; // Resets updateNumber to 50 so an update check will be run.
    checkForUpdate(); // Checks for update.
}
