// **************************************************************************************************************************
// Function: checkForUpdate()
// Checks to see if there is an update available.
// **************************************************************************************************************************
function checkForUpdate() {
    updateNumber++;
    if (updateNumber >= 50) { // Checks if it can run an update check after 50 dashboard shows.
        if (updateCheck == 'Y') { // If user wants updates checked automatically checks for update.
            var feedURL = "http://www.gregsalesto.com/SimpsonsUpdate.html"; // The following code retrieves update information.
            var onloadHandler = function() { xmlLoaded(xmlRequest); };
            var xmlRequest = new XMLHttpRequest();
            xmlRequest.onload = onloadHandler;
            xmlRequest.open("GET", feedURL);
            xmlRequest.setRequestHeader("Cache-Control", "no-cache");
            xmlRequest.send(null);
        }
        else { // If user does not want want updates checked automatically the update notification is hidden if it was visible.
            document.getElementById("UpdateNotification").style.visibility = 'hidden';
        }
        updateNumber = 0; // Resets updateNumber counter since update may have been checked.
    }
}

function xmlLoaded(xmlRequest) 
{
    if (xmlRequest.status == 200) {
        if (xmlRequest.responseText != 4) { // If update notification is anything but a 2 that indicates there is an update.
            document.getElementById("UpdateNotification").style.visibility = 'visible'; // Shows the update notification on screen.
        }
        else if (xmlRequest.responseText == 4) { // If no update available the update notification is hidden.
            document.getElementById("UpdateNotification").style.visibility = 'hidden'; // Hides the update notification.
        }
    }
}

